import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api';

const ItemDetail = () => {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    // Получаем данные предмета
    api.get(`inventory-items/${id}/`).then(response => {
      setItem(response.data);
    }).catch(err => {
      console.error('Ошибка получения предмета:', err);
    });
    // Получаем историю перемещений
    api.get(`inventory-items/${id}/history/`).then(response => {
      setHistory(response.data);
    }).catch(err => {
      console.error('Ошибка получения истории:', err);
    });
  }, [id]);

  if (!item) return <div>Загрузка...</div>;

  return (
    <div>
      <h2>{item.name}</h2>
      <p>Серийный номер: {item.serial_number || 'нет'}</p>
      <p>Тип: {item.item_type}</p>
      <h3>История перемещений</h3>
      <ul>
        {history.map((entry, index) => (
          <li key={index}>
            Локация: {entry.location} – Дата назначения: {entry.assigned_at}
            {entry.removed_at && `, Снято: ${entry.removed_at}`}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ItemDetail;
